import React, { useState } from "react";
import { useForm } from "react-hook-form";

import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";

import {
  Container,
  Typography,
  TextField,
  Button,
  InputAdornment,
  Box,
} from "@mui/material";
import VisibilityIcon from "@mui/icons-material/Visibility";
import VisibilityOffIcon from "@mui/icons-material/VisibilityOff";

interface User {
  firstName: String;
  lastName: String;
  jobTitle: String;
  afm: String;
  salary: Number;
  password: String;
}

const schema = yup.object().shape({
  firstName: yup.string().required(),
  lastName: yup.string().required(),
  jobTitle: yup.string().required(),
  afm: yup.string().required(),
  salary: yup.string().required(),
  password: yup.string().required().min(8).max(120),
});

function AddEmployee() {
  const [showPassword, setShowPassword] = useState(false);

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<User>({ resolver: yupResolver(schema) });

  const handleVisibilityPassword = (): void => {
    setShowPassword((prevPasswordState) => !prevPasswordState);
  };

  return (
    <Container maxWidth="xs">
      <Typography
        variant="h4"
        gutterBottom
        style={{ marginTop: "32px", textAlign: "center" }}
        color="primary"
      >
        Add new Employee
      </Typography>
      <form noValidate>
        <TextField
          {...register("firstName")}
          variant="outlined"
          margin="normal"
          label="First Name"
          fullWidth
          required
          style={{ marginBottom: "12px" }}
        />
        <TextField
          variant="outlined"
          margin="normal"
          label="Last Name"
          fullWidth
          required
          style={{ marginBottom: "12px" }}
        />
        <TextField
          variant="outlined"
          margin="normal"
          label="Job Title"
          fullWidth
          required
          style={{ marginBottom: "12px" }}
        />
        <TextField
          variant="outlined"
          margin="normal"
          label="TIN"
          fullWidth
          required
          style={{ marginBottom: "12px" }}
        />
        <TextField
          variant="outlined"
          margin="normal"
          label="Salary"
          fullWidth
          required
          style={{ marginBottom: "12px" }}
        />
        <TextField
          variant="outlined"
          margin="normal"
          label="Password"
          type={showPassword ? "text" : "password"}
          fullWidth
          required
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                {showPassword ? (
                  <VisibilityIcon
                    style={{ fontSize: "16px", cursor: "pointer" }}
                    onClick={handleVisibilityPassword}
                  />
                ) : (
                  <VisibilityOffIcon
                    style={{ fontSize: "16px", cursor: "pointer" }}
                    onClick={handleVisibilityPassword}
                  />
                )}
              </InputAdornment>
            ),
          }}
        ></TextField>
        <Box
          component="span"
          m={1}
          display="flex"
          justifyContent="space-around"
          alignItems="center"
          style={{ margin: "32px" }}
        >
          <Button
            variant="contained"
            color="primary"
            sx={{ height: 40 }}
            size="large"
          >
            Submit
          </Button>
          <Button
            variant="contained"
            color="secondary"
            sx={{
              height: 40,
              background: "red",
              ":hover": { bgcolor: "#d11a2a" },
            }}
            size="large"
          >
            Cancel
          </Button>
        </Box>
      </form>
    </Container>
  );
}

export default AddEmployee;
